        private static Object getEnumCode(Object e) {
            Object code = null;
            for (String fm : ENUM_PERSIST_FIELD_METHOD.split(",")) {
                try {
                    Method m = e.getClass().getMethod(fm);
                    if (m != null) {
                        code = m.invoke(e);
